import React, { useState, useEffect } from "react";

const Figma = () => {
  const [search, setSearch] = useState("");
  const [searchHistory, setSearchHistory] = useState(["Soup", "Chips", "Nuts", "Vegetable"]);
  const [categories, setCategories] = useState([]); // To store fetched categories
  const [loading, setLoading] = useState(true); // For loading state

  // Fetch the categories data when the component mounts
  useEffect(() => {
    // Assuming the categories.json file is in the public folder
    fetch('/categories.json')
      .then((response) => response.json())
      .then((data) => {
        setCategories(data); // Set the fetched categories data
        setLoading(false); // Stop loading once data is fetched
      })
      .catch((error) => {
        console.error('Error fetching categories:', error);
        setLoading(false);
      });
  }, []);

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (search.trim() && !searchHistory.includes(search)) {
      setSearchHistory([search, ...searchHistory].slice(0, 5));
    }
    setSearch("");
  };

  const filteredCategories = search
    ? categories.filter((category) =>
        category.name.toLowerCase().includes(search.toLowerCase())
      )
    : categories;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      <form onSubmit={handleSearchSubmit} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="What product you like to buy?"
          value={search}
          onChange={handleSearchChange}
          style={{
            padding: "10px",
            width: "100%",
            maxWidth: "400px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        />
      </form>
      <div style={{ marginBottom: "20px" }}>
        <h4>Your Search History</h4>
        {searchHistory.length > 0 ? (
          searchHistory.map((item, index) => (
            <span
              key={index}
              style={{
                display: "inline-block",
                backgroundColor: "#f0f0f0",
                padding: "5px 10px",
                margin: "5px",
                borderRadius: "15px",
                cursor: "pointer",
              }}
              onClick={() => setSearch(item)}
            >
              {item}
            </span>
          ))
        ) : (
          <p>No search history</p>
        )}
      </div>

      <div>
        <h4>Browse by Category</h4>
        {loading ? (
          <p>Loading categories...</p>
        ) : (
          <div style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
            {filteredCategories.length > 0 ? (
              filteredCategories.map((category, index) => (
                <div key={index} style={{ textAlign: "center", width: "100px" }}>
                  <div
                    style={{
                      width: "100px",
                      height: "100px",
                      backgroundColor: "#ddd",
                      borderRadius: "10px",
                      overflow: "hidden",
                    }}
                  >
                    <img
                      src="./logo512.png"
                      alt=""
                      style={{
                        width: "100%",
                        height: "100%",
                        objectFit: "cover",
                      }}
                    />
                  </div>
                  <p style={{ marginTop: "10px" }}>{category.name}</p>
                </div>
              ))
            ) : (
              <p>No categories found.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Figma;
