import React, { useState } from "react";

const Figma= () => {
  const [searchTerm, setSearchTerm] = useState("");
   const [searchHistory, setSearchHistory] = useState(["Soup", "Chips", "Nuts", "Vegetable"]);

  // Categories to display
  const categories = [
    { name: "Vegetables", image: "vegetables.jpg" },
    { name: "Bak Kwa", image: "bak_kwa.jpg" },
    { name: "Soup", image: "soup.jpg" },
    { name: "Finger Food", image: "finger_food.jpg" },
    { name: "Confectionery", image: "confectionery.jpg" },
    { name: "Snacks", image: "snacks.jpg" },
    { name: "Candy & Sweet", image: "candy_sweet.jpg" },
    { name: "Nuts & Seeds", image: "nuts_seeds.jpg" },
    { name: "Fruits", image: "fruits.jpg" },
  ];

  // Handle search input change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handle search submit
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim() && !searchHistory.includes(searchTerm)) {
      setSearchHistory([searchTerm, ...searchHistory].slice(0, 5)); // Add to history, limit to 5 items
    }
    setSearchTerm(""); // Clear search input
  };

  // Filter categories based on search term
  const filteredCategories = searchTerm
    ? categories.filter((category) =>
        category.name.toLowerCase().includes(searchTerm.toLowerCase())
      )
    : categories;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
      {/* Search Bar */}
      <form onSubmit={handleSearchSubmit} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="What product you like to buy?"
          value={searchTerm}
          onChange={handleSearchChange}
          style={{
            padding: "10px",
            width: "100%",
            maxWidth: "400px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        />
      </form>

      {/* Search History */}
      <div style={{ marginBottom: "20px" }}>
        <h4>Your Search History</h4>
        {searchHistory.length > 0 ? (
          searchHistory.map((item, index) => (
            <span
              key={index}
              style={{
                display: "inline-block",
                backgroundColor: "#f0f0f0",
                padding: "5px 10px",
                margin: "5px",
                borderRadius: "15px",
                cursor: "pointer",
              }}
              onClick={() => setSearchTerm(item)} // Click to search again
            >
              {item}
            </span>
          ))
        ) : (
          <p>No search history available.</p>
        )}
      </div>

      {/* Categories */}
      <div>
        <h4>Browse by Category</h4>
        <div style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
          {filteredCategories.length > 0 ? (
            filteredCategories.map((category, index) => (
              <div
                key={index}
                style={{
                  textAlign: "center",
                  width: "100px",
                }}
              >
                <div
                  style={{
                    width: "100px",
                    height: "100px",
                    backgroundColor: "#ddd",
                    borderRadius: "10px",
                    overflow: "hidden",
                  }}
                >
                  <img
                    src={category.image}
                    alt={category.name}
                    style={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                    }}
                  />
                </div>
                <p style={{ marginTop: "10px" }}>{category.name}</p>
              </div>
            ))
          ) : (
            <p>No categories found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Figma;
