import React, { useState } from "react";
import "./index.css"

const products = [
  { name: "Vegetables, John Farm", price: 2000, rating: 4.5, location: "East Coast" },
  { name: "Vegetables, Farmer Farm", price: 7000, rating: 4.5, location: "South North 43" },
  { name: "Organic vegetables", price:3000, rating: 4.5, location: "Marina Centre 755" },
  { name: "Vegetable Cabbage", price: 4000, rating: 4.5, location: "Somerset 213" },
  { name: "Veg YNZ, John Kitchen", price: 500, rating: 4.5, location: "Datapoint 2314" },
];

function ProductFile() {
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("name");
  const [filteredProducts, setFilteredProducts] = useState(products);

  const handleSearch = (e) => {
    const query = e.target.value.toLowerCase();
    setSearchQuery(query);

    const filtered = products.filter((product) =>
      product.name.toLowerCase().includes(query)
    );
    setFilteredProducts(filtered);
  };

  const handleSort = (e) => {
    const sortKey = e.target.value;
    setSortBy(sortKey);

    const sorted = [...filteredProducts].sort((a, b) => {
      if (sortKey === "price") return a.price - b.price;
      if (sortKey === "rating") return b.rating - a.rating;
      return a.name.localeCompare(b.name);
    });

    setFilteredProducts(sorted);
  };

  return (
    <div className="app">
      <h1>Product List</h1>
      <div className="controls">
        <input
          type="text"
          placeholder="Search products..."
          value={searchQuery}
          onChange={handleSearch}
        />
        <select value={sortBy} onChange={handleSort}>
          <option value="name">Sort by Name</option>
          <option value="price">Sort by Price</option>
          <option value="rating">Sort by Rating</option>
        </select>
      </div>
      <div className="product-list">
        {filteredProducts.map((product, index) => (
          <div className="product-card" key={index}>
            <div className="product-image">
                <img  classNamesrc="Group 101825.png" alt="" />
            </div>
            <div className="product-details">
              <h3>{product.name}</h3>
              <p>Price: {product.price.toFixed(2)}</p>
              <p>Rating: ‚≠ê{product.rating}</p>
              <p>Location: {product.location}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductFile;
