import React, { useState } from "react";
import "./index.css";
const Figma= () => {
  const [search, setSearch] = useState("")
  const [searchHistory, setSearchHistory] = useState(["Soup", "Chips", "Nuts", "Vegetable"])

  const categories = [
    { name: "Vegetables"},
  
  ]

  const handleSearchChange = (e) => {
    setSearch(e.target.value)

  }

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (search.trim() && !searchHistory.includes(search)) {
      setSearchHistory([search, ...searchHistory].slice(0, 5))
    }
    setSearch("")
  }

  const filteredCategories = search ? categories.filter((category) =>
        category.name.toLowerCase().includes(search.toLowerCase())
      ): categories

  return (
  <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
 
  <form onSubmit={handleSearchSubmit} style={{ marginBottom: "20px" }}>
  <div className="icon"> <img src="Group 1469.png" alt="icon" srcset="" /></div>
   <input className="input" type="text"  placeholder="write a product"  value={search} onChange={handleSearchChange}/>
      </form>
      <div className="search">
        <h4>Your Search History</h4>
        {searchHistory.length > 0 ? (
          searchHistory.map((item, index) => (
            <span  key={index} className="item" onClick={() => setSearch(item)}>{item}</span>
          ))
        ) : (
          <p>No search history </p>
        )}
      </div>
      <div className="category">
        <h4>Browse by Category</h4>
        <div className="category1">
        {filteredCategories.length > 0 ? (
         filteredCategories.map((category, index) => (
        <div  key={index}
                  style={{
                  textAlign: "center",
                  width: "100px",
                }}>
                <div
                  style={{
                    width: "100px",
                    height: "100px",
                    backgroundColor: "#ddd",
                    borderRadius: "10px",
                    overflow: "hidden",
                  }}>
                  <img  src={category.image} alt={category.name}  style={{
                                                                  width: "100%",
                                                                  height: "100%",
                                                                 objectFit: "cover", }} />
              </div>
                <p style={{ marginTop: "10px" }}>{category.name}</p>
              </div> ))) : (
                           <p>No categories found.</p>
                           )}
          </div>
         </div>
         </div>
  )
}

export default Figma;
